package model;public class Tipo_ocorrencia {    private int id;    private String tipo_evento;    private int gravidade;    public Tipo_ocorrencia(int id, String tipo_evento, int gravidade){        this.id=id;        this.tipo_evento=tipo_evento;        this.gravidade=gravidade;    }    public int getId() {        return id;    }    public String getTipo_evento() {        return tipo_evento;    }    public int getGravidade() {        return gravidade;    }}